module.exports={config:{name:"enchant",version:"1.0",author:"Samir Œ",countDown:5,role:0,shortDescription:"sarcasm",longDescription:"Utility",category:"text",},onStart:async function({api,event,args,message}){try{const content=args.join(" ").toLowerCase();if(!content)return kaguya.reply(`Baka! You must give me something to enchant.`);const enchantmentMap={a:"ᔑ",b:"ʖ",c:"ᓵ",d:"↸",f:"⎓",g:"⊣",h:"⍑",i:"╎",j:"⋮",k:"ꖌ",l:"ꖎ",m:"ᒲ",n:"リ",o:"𝙹",p:"!¡",q:"ᑑ",r:"∷",s:"ᓭ",t:"ℸ ̣",u:"⚍",v:"⍊",w:"∴",x:"·/",y:"||",z:"⨅"};return message.reply(content.replace(/[a-z]/gi,match=>enchantmentMap[match]||match))}catch(err){console.error(err)}},}